mapper.select:
SELECT p.area_id, p.course_id, p.down_hits, p.`name`, p.open_hits, p.school_id, p.type, p.uid, p.`year`, p.question_info FROM tiku_paper p where 1 = 1
and p.course_id = #{courseId}
and p.type = #{paperType}
and p.year = #{year}
and p.area_id = #{area}

SELECT p.uid, p.course_id, p.school_id, p.area_id, p. NAME, p.question_info, p.type, p.`year`, p.down_hits, p.open_hits FROM tiku_paper p WHERE p.area_id = #{areaId}
AND p.type = #{type}
order by down_hits desc
